t.script:
document.addEventListener("DOMContentLoaded", function() {
const form = document.getElementById("propertyForm");
const fileInput = document.getElementById("images");
const previewList = document.getElementById("image-preview-list");
const uploadZone = document.getElementById("uploadZone");
const submitBtn = document.getElementById("submitBtn");
const progressBar = document.getElementById("formProgress");

// Upload Zone Click
if (uploadZone) {
uploadZone.addEventListener("click", function() {
fileInput.click();
});
}

// Drag and Drop
['dragenter', 'dragover', 'dragleave', 'drop'].forEach(eventName => {
uploadZone.addEventListener(eventName, preventDefaults, false);
});

function preventDefaults(e) {
e.preventDefault();
e.stopPropagation();
}

['dragenter', 'dragover'].forEach(eventName => {
uploadZone.addEventListener(eventName, function() {
uploadZone.classList.add('re-drag-active');
});
});

['dragleave', 'drop'].forEach(eventName => {
uploadZone.addEventListener(eventName, function() {
uploadZone.classList.remove('re-drag-active');
});
});

uploadZone.addEventListener('drop', function(e) {
const files = e.dataTransfer.files;
fileInput.files = files;
handleFiles(files);
});

// Input change
fileInput.addEventListener("change", function() {
handleFiles(fileInput.files);
});

// Preview handler
function handleFiles(files) {
previewList.innerHTML = "";
if (!files || files.length === 0) return;

Array.from(files).forEach((file, index) => {
if (file.type.startsWith('image/')) {
const reader = new FileReader();

reader.onload = function(e) {
const div = document.createElement("div");
div.className = "re-preview-item" + (index === 0 ? " re-preview-cover" : "");

div.innerHTML = `












    ${file.name}
    ${formatFileSize(file.size)}
    ${index === 0 ? '


        Cover Image

    ' : ''}

`;

previewList.appendChild(div);
};

reader.readAsDataURL(file);
}
});
}

// Format file size
function formatFileSize(bytes) {
if (bytes === 0) return '0 Bytes';
const k = 1024;
const sizes = ['Bytes', 'KB', 'MB', 'GB'];
const i = Math.floor(Math.log(bytes) / Math.log(k));
return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
}

// Progress tracking
function updateProgress() {
const inputs = form.querySelectorAll('input[required], select[required], textarea[required]');
let filled = 0;

inputs.forEach(input => {
if (input.type === 'file') {
if (input.files & input.files.length > 0) filled++;
} else if (input.value.trim() !== '') {
filled++;
}
});

const progress = (filled / inputs.length) * 100;
progressBar.style.width = progress + '%';
}

form.addEventListener('input', updateProgress);
form.addEventListener('change', updateProgress);

// Submit loading
form.addEventListener("submit", function(e) {
submitBtn.innerHTML = '



Processing...';
submitBtn.style.opacity = '0.75';
submitBtn.style.pointerEvents = 'none';
});

// Focus effect
const inputs = form.querySelectorAll('.re-lux-input');
inputs.forEach(input => {
input.addEventListener('focus', function() {
this.closest('.re-lux-group').classList.add('re-focused');
});
input.addEventListener('blur', function() {
this.closest('.re-lux-group').classList.remove('re-focused');
});
});
});
